<?php
defined('BASEPATH') or exit('No direct script access allowed');

class CompaniesABM extends CI_Controller
{
	function __construct()
	{
		parent::__construct();
		$this->load->model('users');
		$this->load->model('roles');
		$this->load->model('companies');
	}


	public function index()
	{

		if (isset($_SESSION['user_id'])) {

			// ==============================================================
			// get all companies
			// ==============================================================

			$companies = $this->companies->getAllCompaniesByRole($this->session->userdata('role'), $this->session->userdata('user_id')); //
			// ==============================================================
			// end get all companies
			// ==============================================================

			$data = array(
				'breadCrum' => 'companiesABM',
				'breadCrum2' => 'companiesABM',
				'companies' => $companies
			);

			$this->load->view('companiesABM/header');
			$this->load->view('cp/body_start');
			$this->load->view('cp/topbar');
			$this->load->view('cp/leftbar', $data);
			$this->load->view('companiesABM/body', $data);
			$this->load->view('companiesABM/footer');
		} else {
			redirect(base_url());
		}
	}

	public function add($company = '')
	{
		redirect(base_url() . 'companiesABM/companiesAdd/');
	}


