	public function company($id = "")
	{
		if (isset($_SESSION['user_id'])) {
			if ($this->companies->cistc($_SESSION['user_id'], $id)) {
				// ==============================================================
				// get company
				// ==============================================================
				$company = $this->companies->getCompanyById($id);

				// ==============================================================
				// end get company
				// ==============================================================
				// ==============================================================
				// get all users
				// ==============================================================
				$users = $this->users->getAllUsers('5');
				$referals = array();
				$referals = ['' => 'Referido'];
				foreach ($users as $user) {
					$referals += [$user->id => $user->username];
				}
				// ==============================================================
				// end get all users
				// ==============================================================
				// get history
				// ==============================================================
				$history = $this->companies->getHistoryByCompanyId($id);
				// ==============================================================
				// end get history
				// ==============================================================
				// ==============================================================
				// get stores
				// ==============================================================
				$stores = $this->companies->getStoresByCompanyId($id);
				// ==============================================================
				// end get stores
				// ==============================================================
				// ==============================================================
				// get all roles
				// ==============================================================
				$roles = $this->roles->getRolesSelect();
				// ==============================================================
				// end get all roles
				// ==============================================================

				// ==============================================================
				// get referal data
				// ==============================================================
				$referral = $this->users->getUserById($company->id_referral);
				// ==============================================================
				// end get referal data
				// ==============================================================
				// ==============================================================
				// get visitas
				// ==============================================================
				$this->load->model('visitasAux');
				$visitas = $this->visitasAux->getAllVisitasByCompany($company->id);
				// ==============================================================
				// end get visitas
				// ==============================================================
				// ==============================================================
				// get forms
				// ==============================================================
				$this->load->model('formsAux');
				$this->load->model('stores');
				$formsAux = $this->formsAux->getAllFormsByCompany($company->id);
				// ==============================================================
				// end get forms
				// ==============================================================

				// ==============================================================
				// company data array
				// ==============================================================

				$data = array(
					'breadCrum' => 'companiesABM',
					'breadCrum2' => 'companiesABM/companiesAdd',
					'company' => $company,
					'referral' => $referral,
					'referals' => $referals,
					'history' => $history,
					'stores' => $stores,
					'visitas' => $visitas,
					'forms' => $formsAux
				);


				$this->load->view('companiesABM/header');
				$this->load->view('cp/body_start');
				$this->load->view('cp/topbar');
				$this->load->view('cp/leftbar', $data);
				$this->load->view('companiesABM/company', $data);
				$this->load->view('companiesABM/footer');

			} else {
				redirect(base_url());
			}
		} else {
			redirect(base_url());
		}
	}


