	public function companiesReferral()
	{
		if (isset($_SESSION['user_id'])) {
			// ==============================================================
			// get all users
			// ==============================================================
			$users = $this->users->getAllUsers('5');
			$referals = array();
			$referals = ['' => 'Referido'];
			foreach ($users as $user) {
				$referals += [$user->id => $user->username];
			}
			// ==============================================================
			// end get all users
			// ==============================================================
			// ==============================================================
			// get all companies
			// ==============================================================
			$comps = $this->companies->getAllCompanies();
			$companies = array();
			$companies = ['' => 'Compañia'];
			foreach ($comps as $company) {
				$companies += [$company->id => $company->name];
			}
			// ==============================================================
			// end get all companies
			// ==============================================================

			$valueAux = set_value('company_id');
			$options_company = $companies;
			$extraCompany = array(
				'id' => 'company_id',
				'aria-label' => 'company_id',
				'class' => 'form-control form-control-lg',
				'aria-describedby' => 'basic-addon1',
				'class' => 'custom-select select3',
				'required' => ''
			);
			$company_id = array(
				'name' => 'company_id',
				'options' => $options_company,
				'selected' => $valueAux,
				'extra' => $extraCompany
			);


			$valueAux = set_value('id_referral');
			$options_referral = $referals;
			$extraReferral = array(
				'id' => 'id_referral',
				'aria-label' => 'id_referral',
				'class' => 'form-control form-control-lg',
				'aria-describedby' => 'basic-addon1',
				'class' => 'custom-select select3',
				'required' => ''
			);
			$id_referral = array(
				'name' => 'id_referral',
				'options' => $options_referral,
				'selected' => $valueAux,
				'extra' => $extraReferral
			);


			// ==============================================================
			// form validator
			// ==============================================================

			$this->form_validation->set_rules(
				'id_referral',
				'id_referral',
				'required'
			);

			// ==============================================================
			// end new user form data
			// ==============================================================
			$data = array(
				'breadCrum' => 'companiesABM',
				'breadCrum2' => 'companiesABM/companiesReferral',
				'company_id' => $company_id,
				'id_referral' => $id_referral,
				'companies' => $companies,
				'referals' => $referals
			);


			if ($this->form_validation->run() == FALSE) {

				$this->load->view('companiesABM/header');
				$this->load->view('cp/body_start');
				$this->load->view('cp/topbar');
				$this->load->view('cp/leftbar', $data);
				$this->load->view('companiesABM/companiesReferral', $data);
				$this->load->view('companiesABM/footer');
			} else {
				$id_referral = $this->input->post('id_referral');
				$company_id = $this->input->post('company_id');

				$this->companies->insertCompanyReferral($company_id, $id_referral);
				$this->session->set_flashdata('success_msg', '<strong>Exito!</strong> Asociación agregada exitosamente');
				redirect(base_url() . 'companiesABM/companiesReferral');
			}
		} else {
			redirect(base_url());
		}
